mapper.update:
UPDATE tb_user
SET user_yn = 'N'
WHERE user_code = #{user_code}

mapper.select:
SELECT menu_yn
FROM tb_usermenu
WHERE 1=1
AND user_code = #{user_code}
AND menu_url = #{menu_url}

mapper.update:
UPDATE tb_usermenu SET menu_yn = 'Y', update_date = NOW()
WHERE 1=1
AND user_code = #{user_code}
AND menu_url = #{menu_url}

UPDATE tb_usermenu SET menu_yn = 'N'
WHERE 1=1
AND user_code = #{user_code}
AND menu_url = #{menu_url}

mapper.select:
SELECT no, u_code, duty, employee_no, name, education, career, training, cert_name, acquisition_date, next_date, file_name, start_day, note FROM tb_plan_manage
AND name LIKE CONCAT('%', #{name}, '%')
AND start_day >= #{startDate}
AND start_day <= #{endDate}
ORDER BY acquisition_date DESC

mapper.update:
UPDATE tb_work_schedule
date = #{date}, division = #{division}, shift_type = #{shift_type}, line_leader = #{line_leader}, degreasing = #{degreasing}, shot = #{shot}, g800 = #{g800}, g600 = #{g600}, common = #{common}, k_black = #{k_black}, liquid_mgmt = #{liquid_mgmt}, e_coating = #{e_coating}, group_leader = #{group_leader}, rust = #{rust}, lab = #{lab}, memo = #{memo},
WHERE id = #{id}

UPDATE tb_work_handover
date = #{date}, b_f = #{b_f}, ex1 = #{ex1}, ex2 = #{ex2}, ex3 = #{ex3},
WHERE id = #{id}

mapper.select:
SELECT *
FROM tb_standard_doc


    AND mch_name = #{mch_name}



    AND cr_date BETWEEN #{startDate} AND #{endDate}
